import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [data, setData] = useState(0);

  useEffect(() => {
    console.log("useEffect 실행");
  });

  return (
    <div>
      <h1> 데이터 : {data}</h1>
      <button onClick={()=>{setData(data+1)}}>더하기</button>
    </div>
  );
}

export default App;
